function getDomain(raw) {
  if (!raw) return null;
  let s = String(raw).trim();
  if (!s) return null;

  // Add https:// if missing
  if (!/^https?:\/\//i.test(s)) s = `https://${s}`;

  try {
    const u = new URL(s);
    return u.hostname.replace(/^www\./i, '');
  } catch {
    // Tiny fallback: pull host-like token
    const m = s.match(/^(?:https?:\/\/)?(?:www\.)?([^\/?#\s]+)/i);
    return m ? m[1].toLowerCase() : null;
  }
}

for (const item of items) {
  item.json.companyDomain = getDomain(item.json.companyHomePage);
}

return items;

