function extractHeaderLinks(htmlString) {
    const headerLinks = htmlString.match(/<a[^>]+href=["'](.*?)["'][^>]*>(.*?)<\/a>/g);

    if (!headerLinks) return [];

    return headerLinks.map(link => {
        const urlMatch = link.match(/href=["'](.*?)["']/);
        const textMatch = link.match(/>(.*?)<\/a>/);
        return {
            text: textMatch ? textMatch[1].trim() : '',
            url: urlMatch ? urlMatch[1] : ''
        };
    }).filter(item => item.url); // Remove any empty URLs
}

return extractHeaderLinks(inputData);

