const htmlContent = $node["Header Links Scraper"].json["data"]; // Your HTML input

// Function to strip HTML and clean text for JSON-safe plain text
const extractPlainText = (html) => {
    if (!html) return "";

    // Remove <script> and <style> content
    const withoutScriptsAndStyles = html.replace(/<script[\s\S]*?>[\s\S]*?<\/script>/gi, "")
                                        .replace(/<style[\s\S]*?>[\s\S]*?<\/style>/gi, "");

    // Strip all HTML tags
    const strippedText = withoutScriptsAndStyles.replace(/<\/?[^>]+(>|$)/g, "");

    // Replace HTML entities
    const decodedText = strippedText.replace(/&nbsp;/gi, " ")
                                    .replace(/&amp;/gi, "&")
                                    .replace(/&lt;/gi, "<")
                                    .replace(/&gt;/gi, ">")
                                    .replace(/&quot;/gi, "") // double quote
                                    .replace(/&#39;/gi, "");  // single quote

    // Remove all actual " and ' characters from text
    const noQuotes = decodedText.replace(/["']/g, "");

    // Collapse whitespace
    const cleanedText = noQuotes.replace(/\s+/g, " ").trim();

    return cleanedText;
};

// Run it
const plainText = extractPlainText(htmlContent);

return {
    plainText: plainText,
};

