// 1. Retrieve product name (adjust field reference as needed)
const productName = $('Get Campaign Record').first().json['Product Name'][0] || 'UnknownProduct';

// 2. Clean the product name: remove non-alphanumeric characters
// - Remove spaces, dashes, punctuation, special symbols
// - Capitalize the first letter of each word (optional for readability)
const cleanedName = productName
  .replace(/[^a-zA-Z0-9]/g, ' ') // Replace any non-alphanumeric with space
  .split(' ')
  .filter(Boolean) // Remove empty entries
  .map(word => word.charAt(0).toUpperCase() + word.slice(1)) // Capitalize
  .join(''); // Join without spaces

// 3. Generate timestamp (format: YYYYMMDD_HHmmss)
const timestamp = new Date().toISOString().replace(/[-:T]/g, '').split('.')[0];

// 4. Build the file name
const fileName = `${cleanedName}_${timestamp}.mp4`;

// 5. Return the filename as output
return [
  {
    json: {
      originalProductName: productName,
      cleanedProductName: cleanedName,
      timestamp,
      fileName
    }
  }
];

