parameters.jsCode:
const item = $input.item;

const base64String = item.json.data[0].b64_json;

if (!base64String) {
  console.error("Base64 string not found at the expected path.");
  return [{ json: { error: "Base64 string not found" } }];
}

try {
  const outputItem = {
    json: {},

    binary: {
      binaryImageData: {
        data: base64String,
        fileName: 'generated_image.png',
        mimeType: 'image/png'
      }
    }
  };

  return [outputItem];

} catch (error) {
  console.error("Error preparing binary output structure:", error);
  return [{ json: { error: "Failed to prepare binary output", details: error.message } }];
}
