// Initialize base64Credentials as an empty string
let base64Credentials = '';

// Retrieve the API credentials (apiKey and apiSecret) from the input
const apiKey = $('Set Values').first().json.apiKey;
const apiSecret = $('Set Values').first().json.apiSecret;

// Check if the credentials are present
if (apiKey && apiSecret) {
  // Combine the API Key and API Secret into a string and Base64 encode it
  base64Credentials = Buffer.from(`${apiKey}:${apiSecret}`).toString('base64');
}

// Return the response with both the base64 credentials and image URL
return [
  {
    json: {
      base64Credentials
    }
  }
];

// Get the HTML output from the previous AI agent module
const htmlOutput = $input.first().json.output; // Assuming this is the raw HTML output

// Regular expression to match the content inside the <title> tag
const match = htmlOutput.match(/<title>(.*?)<\/title>/);

// Extract the title or use 'Title not found' as default
const title = match ? match[1] : 'Title not found';

// Sanitize the title: Remove any character that is not a-z, A-Z, 0-9
const sanitizedTitle = title.replace(/[^A-Za-z0-9]/g, '');

// Get the current timestamp in YYYYMMDD_HHmmss format
const timestamp = new Date().toISOString().replace(/T/, '_').replace(/:/g, '').split('.')[0];

// Create the PDF file name
const pdfFileName = `${sanitizedTitle}_${timestamp}.pdf`;

// Return the title and pdfFileName
return [
  {
    json: {
      title: title,
      pdfFileName: pdfFileName
    }
  }
];
