// Get old notes from a previous node
const oldNotes = $('Get Airtable Lead').first().json.Notes;

// Define your new note manually
let newNotes = "Personalization completed.";

// Format datetime as MM.DD.YYYY HH:mm
const now = new Date();
const timestamp =
  String(now.getMonth() + 1).padStart(2, '0') + "." +
  String(now.getDate()).padStart(2, '0') + "." +
  now.getFullYear() + " " +
  String(now.getHours()).padStart(2, '0') + ":" +
  String(now.getMinutes()).padStart(2, '0');

// Prepend timestamp to the new note
newNotes = `${timestamp}- ${newNotes}`;

// If old notes exist, append with a carriage return
const updatedNotes = oldNotes ? `${oldNotes}\n${newNotes}` : newNotes;

// ✅ Return as an object under "Notes"
return [
  {
    json: {
      Notes: updatedNotes
    }
  }
];

const campaignId = $('Webhook').first().json.body.vars.INSTANTLY_CAMPAIGN_ID;
const email = $('Get Airtable Lead1').first().json.Email;
const phone = $('Get Airtable Lead1').first().json.Phone;
const personalization = $('Get Airtable Lead1').first().json.Icebreaker;
const website = $('Get Airtable Lead1').first().json['Home Page'];
const industry = $('Get Airtable Lead1').first().json['Industry'];
const mediaLink = $('Webhook').first().json.body.vars.KAP_AUTOMATIONS_LINK;
const mediaLink2 = $('Webhook').first().json.body.vars.YOUTUBE_MEDIA_LINK_1;
const typeformBaseUrl = $('Webhook').first().json.body.vars.TYPEFORM_SURVEY_LINK;
const calendarLink = $('Webhook').first().json.body.vars.CALENDAR_LINK;
const rawVerify = $('Webhook').first().json.body.vars.VERIFY_LEADS_ON_IMPORT;
const verifyLeadsOnImport = ['true', '1', 'yes'].includes(String(rawVerify).toLowerCase());
const source = 'Instantly.ai';


let companyName = $('Get Airtable Lead1').first().json['Company Name'];
let cleanCompanyName = $('Get Airtable Lead1').first().json['Clean Company Name'];

const fullName = $('Get Airtable Lead1').item.json.Name || "";
let firstName = "";
let lastName = "";

// Manage name
const nameParts = fullName.trim().split(" ");
if (nameParts.length > 0) {
  firstName = nameParts[0];
}
if (nameParts.length > 1) {
  lastName = nameParts.slice(1).join(" "); // handles multi-word last names
}

if (cleanCompanyName && cleanCompanyName.trim() !== '') {
  companyName = cleanCompanyName;
}

const mediaLink3 = `${typeformBaseUrl}#first_name=${encodeURIComponent(firstName)}&last_name=${encodeURIComponent(lastName)}&email=${encodeURIComponent(email)}&phone_number=${encodeURIComponent(phone)}&company_name=${encodeURIComponent(companyName)}&utm_sourcee=${encodeURIComponent(source)}`;

// === Payload ===
const payload = {
  "campaign": campaignId,
  "email": email,
  "personalization": personalization,
  "last_name": lastName,
  "first_name": firstName,
  "company_name": companyName,
  "website": website,
  "phone": phone,
  "skip_if_in_list": true,
  "skip_if_in_campaign": true,
  "verify_leads_on_import": verifyLeadsOnImport,
  "custom_variables": {
    "industry": industry,
    "mediaLink": mediaLink,
    "mediaLink2": mediaLink2,
    "mediaLink3": mediaLink3,
    "calendarLink": calendarLink,
  }
}

return [{ json: payload }];

