// ---- Collectors
const steps = [];
const tokens = [];
let totalTokens = 0;
let agentMessage = "";

// Try to get `output` from a step.observation JSON string
const extractOutputFromObservation = (obs) => {
  if (typeof obs !== 'string') return null;
  try {
    const parsed = JSON.parse(obs);
    if (Array.isArray(parsed) && parsed.length && typeof parsed[0]?.output === 'string') {
      return parsed[0].output;
    }
    if (parsed && typeof parsed.output === 'string') {
      return parsed.output;
    }
  } catch (_) {}
  return null;
};

// Iterate inputs once
for (const { json: data } of $input.all()) {
  // Prefer direct output when present
  if (data && typeof data.output === 'string' && !agentMessage) {
    agentMessage = data.output;
  }

  const stepsArr = data?.intermediateSteps;
  if (Array.isArray(stepsArr)) {
    for (const step of stepsArr) {
      if (step?.action) {
        steps.push({
          tool: step.action.tool,
          toolInput: step.action.toolInput,
          observation: step.observation,
        });
      }

      // Token usage
      const msgLog = step?.action?.messageLog;
      if (Array.isArray(msgLog)) {
        for (const message of msgLog) {
          const usage = message?.kwargs?.response_metadata?.usage;
          if (usage) {
            tokens.push({
              prompt_tokens: usage.prompt_tokens,
              completion_tokens: usage.completion_tokens,
              total_tokens: usage.total_tokens,
              model_name: message.kwargs.response_metadata.model_name,
            });
            if (typeof usage.total_tokens === 'number') totalTokens += usage.total_tokens;
          }
        }
      }

      // If we still don't have the readable text, try to extract it from observation
      if (!agentMessage) {
        const fromObs = extractOutputFromObservation(step?.observation);
        if (fromObs) agentMessage = fromObs;
      }
    }
  }
}

// Build outputs
const plainText = agentMessage || "";

// MarkdownV2 code block wrapper (nothing inside needs escaping)
const toMdV2CodeBlock = (s) => {
  // Neutralize any accidental ``` inside the content to keep the fence intact
  const safe = String(s).replace(/```/g, '\\`\\`\\`');
  return '```\n' + safe + '\n```';
};
const mdv2CodeBlock = agentMessage ? toMdV2CodeBlock(agentMessage) : '';

return [{
  json: {
    steps,
    tokens,
    total_tokens: totalTokens,
    plainText,     // Use when Parse Mode is removed/blank (no markdown)
    mdv2CodeBlock, // Use when Parse Mode = MarkdownV2
  }
}];

