// Apollo Search URL generator (Function Item)

function buildApolloUrl(data) {
  const baseUrl = "https://app.apollo.io/#/people";

  const params = [];

  // Helper: split on commas, trim, drop empties
  const splitList = (str) =>
    (str || "")
      .split(",")
      .map(s => s.trim())
      .filter(Boolean);

  // --- Job Titles ---
  for (const title of splitList(data["Job Title"])) {
    params.push(`personTitles[]=${encodeURIComponent(title)}`);
  }

  // --- Company Size ---
  if (data["Company Size"]) {
    params.push(`organizationNumEmployeesRanges[]=${encodeURIComponent(data["Company Size"].trim())}`);
  }

  // --- Keywords ---
  for (const kw of splitList(data["Keywords"])) {
    params.push(`qOrganizationKeywordTags[]=${encodeURIComponent(kw)}`);
  }

  // --- Locations ---
  for (const loc of splitList(data["Location"])) {
    params.push(`personLocations[]=${encodeURIComponent(loc)}`);
  }

  // --- Industries ---
  for (const ind of splitList(data["Industry"])) {
    params.push(`organizationIndustries[]=${encodeURIComponent(ind)}`);
  }

  // --- Seniorities ---
  for (const seniority of splitList(data["Seniority"])) {
    params.push(`personSeniorities[]=${encodeURIComponent(seniority)}`);
  }

  // --- Has Email (boolean flag) ---
  if (data["Has Email"] && String(data["Has Email"]).toLowerCase() === "true") {
    params.push("contactEmailStatus[]=verified");
  }

  return `${baseUrl}?${params.join("&")}`;
}

// Build the URL from this item’s JSON
const url = buildApolloUrl($json);

return {
  json: {
    apolloUrl: url
  }
};
