function generateId() {
  const timestamp = Date.now().toString(36);
  const random = Math.random().toString(36).substring(2, 10);
  return `${timestamp}-${random}`;
}

// --- CONFIGURATION ---
const recordLimit = $('Get Record Count1').first().json.Value;

// --- MAIN LOGIC ---
// This Code node takes the full array (from HTTP Request)
// and reformats each entry into a standardized structure
// for downstream looping

const inputArray = $input.all();
const results = [];
const leadSource = $('Set Search Parameters').first().json.leadSource || "";
const maxNewLeads = $('Set Variables').first().json.maxNewLeads;
const linkToBulkProcessing = $('Get Link to Bulk Processing1').first().json.Value || "";

for (const item of inputArray) {
  const records = Array.isArray(item.json) ? item.json : [item.json];

  for (const rec of records) {
    if (results.length >= recordLimit) break;

    // --- Normalize header keys ---
    // Converts "Company Name" → "company_name", "Job Title" → "job_title", etc.
    const normalized = Object.fromEntries(
      Object.entries(rec).map(([key, val]) => [
        key.trim().replace(/\s+/g, "_").toLowerCase(),
        val,
      ])
    );

    results.push({
      json: {
        record_id: "",
        person_id: normalized.person_id || generateId(),
        name: `${normalized.first_name || ""} ${normalized.last_name || ""}`.trim(),
        email: normalized.email || "",
        company_name: normalized.company_name || "",
        home_page: normalized.company_website || "",
        company_linkedin: normalized.company_linkedin || "",
        personal_linkedin: normalized.linkedin || "",
        headline: normalized.headline || "",
        industry: normalized.industry || "",
        employees_count: normalized.employees_count || "",
        city: normalized.city || "",
        state: normalized.state || "",
        country: normalized.country || "",
		employees_count: normalized.employees_count || "",
        title: normalized.title || normalized.job_title || "",
        company_desc: normalized.company_description || "",
